	pub_key_ptr->params = params_ptr;
	pub_key_ptr->y.crv = &params_ptr->ec_curve;
	dpu_val3 = *(uint32_t *)(uintptr_t *)&params_ptr->ec_gen.X;
	//printf ("pub_key->params 0x%x\n", pub_key_ptr->params);

	ret = generic_ec_verify(ecdsa_secp256r1_test_vectors_expected_sig, sizeof(ecdsa_secp256r1_test_vectors_expected_sig), pub_key_ptr, msg, sizeof(msg),
				 ECDSA, SHA256, NULL, 0);
	//printf ("oh that's wonderful %d\n", ret);
#endif
	return 1;

